    render(){
        return(
            <div className='Formuler.css'>
                <Header />
                <input type="text" onChange={this.Handelchange} value={this.state.inputname}/>
                <button onClick={()=>{
                    this.props.sendToArray(this.state.inputname)
                    this.setState({inputname:''})
                }}>send to todo</button>
                <ListTodo />
            </div>
        )
    }
}

