import React from 'react'
import './Todo.css'
import Header from './Header'
import AufgabenListe from './AufgabenListe'

class TodoApp  extends React.Component{
    constructor(props){
        super(props)
        this.state={
          aufgaben:['aufgabe 1', 'blumen gießen', 'kochen', 'sport'],
          inputTexte:""
        }

        this.aufgabeHinzufügen=this.aufgabeHinzufügen.bind(this)
        this.handelchange=this.handelchange.bind(this)
    }


    handelchange(event){
      this.setState=({inputTexte:event.target.value})
    }

  aufgabeHinzufügen() {
    this.setState(state => ({
      aufgaben: [...state.aufgaben, this.state.inputTexte],
      inputTexte: ""
    })
    )
  } 

render(){
    return(
    <div className="Todo">
      <Header />
      <form action="">
            <fieldset>
                <legend>Neue Aufgabe .</legend>
                <div><input type="text" onChange={this.handelchange}></input></div>
                <br/>
                <div><button onClick={this.aufgabeHinzufügen}>Aufgabe Hinzufügen</button></div>

            </fieldset>

        </form>
        <AufgabenListe aufgaben={['aufgabe 1', 'blumen gießen', 'kochen', 'sport']}  />

    </div>
    )
  }
}
export default TodoApp;
