const initialState = {

    inputTexte:''
  }

  // die Funktion für den reducer:

  export default function reducer (state = initialState, action){
    switch(action.type){
      case 'AUFGABE_HINZUFÜGEN': 
        return { ...state, zahl: state.zahl + 1 }

      default:
        return state;
    }
  }
