const initialState = {
    aufgabe:[],

  }

  // die Funktion für den reducer:

  export default function reducer (state = initialState, action){
    switch(action.type){
      case 'SEND_TOARRAY': 
        return { ...state,aufgabe:[...state,action.peyload]}

      default:
        return state;
    }
  }
