import React from 'react'
import {connect} from 'react-redux'
class TodoRedux extends React.Component{

    constructor(props){
        console.log(props);
        super(props)
        this.state={
            inputname:''
        }
        this.Handelchange=this.Handelchange.bind(this)
    }

Handelchange(event){
    this.setState({inputname:event.target.value})
}
    render(){
        return(
            <div>
                <input type="text" onchange={this.Handelchange}/>
                <button onClick={this.props.sendToArray(this.state.inputname)}>send to todo</button>

            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => ({
    sendToArray: (data) => dispatch({type:'SEND_TOARRAY',peyload:data})
})



export default connect(null,mapDispatchToProps)(TodoRedux)
