const initialState = {
    aufgabe:[],
    inputTexte:''
  }

  // die Funktion für den reducer:

  export default function reducer (state = initialState, action){
    switch(action.type){
      case 'AUFGABE_HINZUFÜGEN': 
        return { ...state,  }

      default:
        return state;
    }
  }
