import React from 'react'
import Header from './Header'
import AufgabenListe from './AufgabenListe'
import Aufgabeinput from './Aufgabeinput'

class TodoApp  extends React.Component{
    constructor(props){
        super(props)
        this.state={
          aufgaben:['aufgabe 1', 'blumen gießen', 'kochen', 'sport'],
          inputTexte:""
        }

        this.aufgabeHinzufügen=this.aufgabeHinzufügen.bind(this)
        this.handelchange=this.handelchange.bind(this)
        this.löschHandler=this.löschHandler(this)
    }


    handelchange(event){
      this.setState({inputTexte:event.target.value})
    }

  aufgabeHinzufügen(event) {
    event.preventDefault();
    this.setState(state => ({
      aufgaben: [...state.aufgaben, this.state.inputTexte],
      inputTexte: ""
    })

    )
  } 

  löschHandler(event){
    let löschid=Number.parseInt(event.target.id.slice("todoitem".length)) 
    console.log('item gelöscht',löschid);
  }

  componentDidUpdate(){
    console.log("Todo-App wurde neu gerendert.");
    localStorage.setItem("todoaufgaben",JSON.stringify(this.state.aufgaben))
  }

  componentDidMount(){
    console.log("ToDo-App geboren und einsatzbereit.");
    let aufgaben=localStorage.getItem("todoaufgaben")
    if(aufgaben){
      let todos=JSON.parse(aufgaben)
      this.setState({aufgaben:todos})
    }
  }

render(){
    return(
    <div className="Todo">
      <Header />
      <Aufgabeinput aufgabeHinzufügen={this.aufgabeHinzufügen} handelchange={this.handelchange} inputTexte={this.state.inputTexte}/>
      <AufgabenListe aufgaben={this.state.aufgaben} löschHandler={this.löschHandler} />

    </div>
    )
  }
}
export default TodoApp;
